select ranges."range" as kvadratura, count(*) as broj_nekretnina from (select 0 minRange, 35 maxRange, '<35' "range" union all select 36, 50,'36-50' union all select 51, 65, '51-65' union all select 66, 80, '66-80' union all select 81, 95,'81-95' union all select 96, 110,'96-110' union all select 111, 1000000000, '>111') as ranges left join  nekretnine_info on nekretnine_info.kvadratura_m2 between ranges.minRange and ranges.maxRange where nekretnine_info.kategorija = 'stan' group by ranges.range;
