select ranges."range" as dekada, count(*) as broj_nekretnina from (select 1951 minRange, 1960 maxRange, '1951-1960' "range" union all select 1961, 1970,'1961-1970' union all select 1971, 1980, '1971-1980' union all select 1981, 1990, '1981-1990' union all select 1991, 2000,'1991-2000' union all select 2001, 2010,'2001-2010' union all select 2011, 2020, '2011-2020') as ranges left join  nekretnine_info on nekretnine_info.godina between ranges.minRange and ranges.maxRange group by ranges.range;
