select ranges."range" as cena, count(*) as broj_nekretnina from (select 0 minRange, 49999 maxRange, '<49999' "range" union all select 50000, 99999,'50000-99999' union all select 100000, 149999, '100000-149999' union all select 150000, 199999, '150000-199999' union all select 20000, 1000000000,'>200000') as ranges left join  nekretnine_info on nekretnine_info.cena_eur between ranges.minRange and ranges.maxRange group by ranges.range;
